import React from "react";
import Card from "react-bootstrap/Card";
import ListGroup from "react-bootstrap/ListGroup";
import "./MovieItem.css";
function MovieItem({ movie }) {
  return (
    <>
      <Card style={{ width: "18rem" }}>
        <Card.Img variant="top" src={movie.moviemainphotos} />
        <Card.Body>
          <Card.Title>{movie.movietitle}</Card.Title>
        </Card.Body>
        <ListGroup className="list-group-flush">
          <ListGroup.Item>Language: {movie.movielanguages}     </ListGroup.Item>
          <ListGroup.Item>Country: {movie.moviecountries}</ListGroup.Item>
          <ListGroup.Item> {movie.moviegenres}</ListGroup.Item>
          <ListGroup.Item> {movie.imdbmovieid}</ListGroup.Item>

        </ListGroup>

      </Card>

      <div className="movie-item">
        {console.log({ movie })}

        <img src={movie.moviemainphotos} alt={movie.movietitle} />
        <div className="movie-details">
          <h2>{movie.movietitle}</h2>
          <p>Language: {movie.movielanguages}</p>
          <p>Country: {movie.moviecountries}</p>
          <p>Genre: {movie.moviegenres}</p>
          <p>imdbmovieid : {movie.imdbmovieid}</p>
        </div>
      </div>
    </>
  );
}

export default MovieItem;
