import React, { useState } from 'react';
import MovieList from './components/MovieList';
import Filter from './components/Filter';
import movies_Data from './data/movies_Data.js';

function App() {
  const [movies, setMovies] = useState(movies_Data);

  const filterMovies = (filters) => {
    const filteredMovies = movies_Data.filter(movie => {
      return Object.keys(filters).every(key => {
        if (filters[key] === 'All') return true;
        return movie[key] === filters[key];
      });
    });
    setMovies(filteredMovies);
  };

  return (
    <div>
      <h1>Movie App</h1>
      <Filter filterMovies={filterMovies} />
      <MovieList movies={movies} />
    </div>
  );
}

export default App;
